import { useLocation } from "react-router-dom";

const Web3 = require("web3");

const userInfo = async (accountAddress) => {
    let _web3 = new Web3();
    const result = await _web3.eth.getBalance(accountAddress);
    const resultFormated = _web3.utils.fromWei(result);
    console.log(resultFormated);
    return resultFormated;
}

function WalletPage() {
    const data = useLocation().state;
    const accountAddress = data.account;

    return (
        <div>
            <h1>{accountAddress}</h1>
            <h3>{userInfo(accountAddress)}</h3>
        </div>
    );
}

export default WalletPage;
